export const PopupWithForm = (props) => {
    return(
        <section className={`popup popup-${props.name} ${props.isOpen ? 'popup_opened' : ''}`} id={props.name} onClick={props.onClose}>
    <div className="popup__content">
      <h2 className="popup__title">{props.title}</h2>
      <button className="popup__icon-close" type="button" />
      <form className="popup__form" name={props.name} noValidate="">
        <button className={`popup__button-save popup__button-save_${props.name}`} type={props.title}>{props.btnName}
          Сохранить
        </button>
      </form>
    </div>
  </section>
    )
}
