import logo from '../images/logo.svg';
import { Link } from 'react-router-dom';

function Header({ isLoggedIn, email, onSignout }) {

  function renderHeaderMenu() {
    return (
      <div className={`header__data ${isDisplayMobileAndRootHref() && 'header__data_display_mobile'}`}>
        {
          isRootHref
            ? <>
              <p className='header__email'>{userData.email}</p>
              <button
                className='header__btn'
                type='button'
                aria-label='Выход из личного кабинета'
                onClick={signUserOut}
              >
                Выйти
              </button>
            </>
            : <Link
              className='header__btn'
              to={isLoginHref ? './sign-up' : './sign-in'}
            >
              {isLoginHref ? 'Регистрация' : 'Войти'}
            </Link>
        }
      </div>
    );
  };

  return (
    <header className="header">
      <div className="header__logo" />
      <nav className='header__nav'>
        <ul className='header__list'>
          <li>
            <div className='header__user'>{isLoggedIn ? email : ''}</div>
          </li>
          <li>
            <Link
              to='/'
              className='header__button'
              onClick={onSignout}>
              {isLoggedIn ? 'Выйти' : ''}
            </Link>
            <Link to={isLoginHref ? "./sign-up" : "./sign-in"} className="header__nav-link"
            >
             {isLoginHref ? "Регистрация" : "Войти"}
            </Link>
            {/* <Link to="/sign-in" className="header__nav-link">
              Войти
            </Link> */}
          </li>
        </ul>
      </nav>
    </header>
  )
}

export default Header;
