import React, { useEffect, useState } from "react";
import { api } from '../utils/api';

export const Main = ({ onEditProfile, onAddPlace, onEditAvatar, onCardClick }) => {
    const [userName, setUserName] = useState('');
    const [userDescripttion, setUserDescription] = useState('');
    const [userAvatar, setUserAvatar] = useState('');
    const [cards, setCards] = useState([])
    useEffect(() => {
        api.getUserInfo().then(res => {
            const userInfo = res
            setUserName(userInfo.name)
            setUserDescription(userInfo.about)
            setUserAvatar(userInfo.avatar)
        })

        api.getAddingPictures().then(res => {
            const getedCards = res
            setCards(getedCards)
        })
    },{})


    return (
        <main className="main">
        <section className="profile">
          <div className="profile__content">
            <img
              src={userAvatar}
              className="profile__avatar"
              alt="фото пользователя"
            />
            <div className="profile__info">
              <h1 className="profile__name">{userName}</h1>
              <button
                className="profile__edit-button profile__edit-button_opened"
                type="button" onClick={onEditProfile}
              />
              <p className="profile__about">Исследователь океана</p>
            </div>
          </div>
          <button
            className="profile__add-button profile__add-button_opened"
            type="button" onClick={onAddPlasile}
          />
        </section>
        <ul className="elements"></ul>
      </main>
    )
}
