// 

import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import logo from "../images/logo.svg";

function Register (props) {
  // Стейты для регистрации
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  // Передача почты и пароля
  function handleEmail (event) { setEmail(event.target.value) }
  function handlePass (event) { setPassword(event.target.value) }
  // Обработка почты и пароля через API (utils/AuthApi), функция регистрации в App
  function handleSubmitButton (event) {
    event.preventDefault();
    props.handleRegister(password, email);
    setPassword('');
    setEmail('');
  }

    return (
        <>
          <header className="header">
            <img className="header__logo" src={logo} alt="лого место" />
            <Link to="/sign-in" className="header__nav-link">
              Войти
            </Link>
          </header>

          <form className="entry-form" onSubmit={handleSubmitButton}>
            <h2 className="entry-form__title">Регистрация</h2>
            <input
              // value={formParams.email}
              onChange={handleEmail}
              name="email"
              type="email"
              className="entry-form__input"
              placeholder="Email"
              autoComplete="off"
            ></input>
            <span className="entry-form__input-error"></span>
            <input
              // value={formParams.password}
              onChange={handlePass}
              name="password"
              type="password"
              className="entry-form__input"
              placeholder="Пароль"
              autoComplete="off"
            ></input>
            <span className="entry-form__input-error"></span>
            <button type="submit" className="entry-form__button">
              Зарегистрироваться
            </button>
            <div className="register-signin">
              <p className="register-signin__p">Уже зарегистрированы?</p>
              <Link to="/sign-in" className="register-signin__link">
                Войти
              </Link>
            </div>
          </form>
        </>
      );
    }

export default Register;
